import React, { useState } from "react";
import { useSelector } from "react-redux";
import Widget from "./Widget";
import AddWidgetForm from "./AddWidgetForm";

const Dashboard = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const categories = useSelector((state) => state.dashboard.categories);

  // Filter widgets inside each category based on the search term
  const filteredCategories = categories.map((category) => ({
    ...category,
    widgets: category.widgets.filter((widget) =>
      widget.name.toLowerCase().includes(searchTerm.toLowerCase()) && widget.displ
    ),
  }));

  return (
    <div className="dashboard">
      {/* Search Input */}
      <input
        type="text"
        placeholder="Search widgets..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {/* Render only categories with matching widgets */}
      {filteredCategories
        .filter((category) => category.widgets.length > 0) // Only render categories that have widgets
        .map((category) => (
          <div key={category.id} className="category">
            <h2>{category.name}</h2>
            <div className="widgets">
              {category.widgets.map((widget) => (
                <Widget
                  key={widget.id}
                  widget={widget}
                  categoryId={category.id}
                />
              ))}
            </div>
            <AddWidgetForm categoryId={category.id} />
          </div>
        ))}

      {/* Show a message if no widgets match the search */}
      {filteredCategories.every((category) => category.widgets.length === 0) && (
        <p>No widgets match your search.</p>
      )}
    </div>
  );
};

export default Dashboard;
