import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Widget from './Widget';
import { addWidget } from './redux/'; // Assuming addWidget action
import WidgetModal from './WidgetModal';

const Dashboard = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedCategoryId, setSelectedCategoryId] = useState(null);
  const categories = useSelector((state) => state.dashboard.categories);
  const dispatch = useDispatch();

  const handleOpenModal = (categoryId) => {
    setSelectedCategoryId(categoryId);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  const handleAddWidget = (widgetName) => {
    if (selectedCategoryId) {
      const newWidget = {
        id: Math.random(), // Replace with a real unique ID logic
        name: widgetName,
      };
      dispatch(addWidget(newWidget, selectedCategoryId));
    }
  };

  return (
    <div className="dashboard">
      {categories.map((category) => (
        <div key={category.id} className="category">
          <h2>{category.name}</h2>
          <div className="widgets">
            {category.widgets.map((widget) => (
              <Widget key={widget.id} widget={widget} />
            ))}
          </div>
          <button onClick={() => handleOpenModal(category.id)}>
            Add Widget
          </button>
        </div>
      ))}

      {/* Widget modal */}
      <WidgetModal
        isOpen={isModalOpen}
        onClose={handleCloseModal}
        onSubmit={handleAddWidget}
      />
    </div>
  );
};

export default Dashboard;
