import React, { useState } from 'react';

const AddWidgetModal = ({ isOpen, onClose, onConfirm, widgets }) => {
  const [activeTab, setActiveTab] = useState('CSPM'); // Default tab
  const [selectedWidgets, setSelectedWidgets] = useState([]);

  if (!isOpen) return null;

  const handleTabChange = (tab) => setActiveTab(tab);

  const handleWidgetSelect = (widget) => {
    setSelectedWidgets((prevSelected) =>
      prevSelected.includes(widget)
        ? prevSelected.filter((w) => w !== widget)
        : [...prevSelected, widget]
    );
  };

  const handleConfirm = () => {
    onConfirm(selectedWidgets);
    setSelectedWidgets([]);
    onClose();
  };

  return (
    <div className="modal-container">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Add Widget</h2>
          <button className="close-button" onClick={onClose}>X</button>
        </div>

        <p>Personalise your dashboard by adding the following widget</p>

        {/* Tabs */}
        <div className="tabs">
          {['CSPM', 'CWPP', 'Image', 'Ticket'].map((tab) => (
            <button
              key={tab}
              className={`tab-button ${activeTab === tab ? 'active' : ''}`}
              onClick={() => handleTabChange(tab)}
            >
              {tab}
            </button>
          ))}
        </div>

        {/* Widget List for Selected Tab */}
        <div className="widget-list">
          {widgets[activeTab]?.map((widget) => (
            <div key={widget.id} className="widget-item">
              <input
                type="checkbox"
                checked={selectedWidgets.includes(widget)}
                onChange={() => handleWidgetSelect(widget)}
              />
              <span>{widget.name}</span>
            </div>
          ))}
        </div>

        {/* Footer Buttons */}
        <div className="modal-footer">
          <button className="cancel-button" onClick={onClose}>Cancel</button>
          <button className="confirm-button" onClick={handleConfirm}>Confirm</button>
        </div>
      </div>
    </div>
  );
};

export default AddWidgetModal;
