import React, { useState } from "react";
import { useSelector } from "react-redux";
import Widget from "./Widget";
import AddWidgetModal from "./WidgetModal.j";

const Dashboard = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const categories = useSelector((state) => state.dashboard.categories);

  // Filter widgets inside each category based on the search term
  const filteredCategories = categories.map((category) => ({
    ...category,
    widgets: category.widgets.filter((widget) =>
      widget.name.toLowerCase().includes(searchTerm.toLowerCase())
    ),
  }));

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [widgets] = useState({
    CSPM: [
      { id: 1, name: 'Widget 1' },
      { id: 2, name: 'Widget 2' }
    ],
    CWPP: [
      { id: 3, name: 'Widget 3' },
      { id: 4, name: 'Widget 4' }
    ],
    Image: [
      { id: 5, name: 'Widget 5' },
      { id: 6, name: 'Widget 6' }
    ],
    Ticket: [
      { id: 7, name: 'Widget 7' },
      { id: 8, name: 'Widget 8' }
    ]
  });

  const handleAddWidgetClick = () => setIsModalOpen(true);
  const handleModalClose = () => setIsModalOpen(false);
  const handleConfirm = (selectedWidgets) => {
    console.log('Selected widgets:', selectedWidgets);
    setIsModalOpen(false);
  };

  return (
    <div className="dashboard">
      {/* Search Input */}
      <input
        type="text"
        placeholder="Search widgets..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {/* Render only categories with matching widgets */}
      {filteredCategories
        .filter((category) => category.widgets.length > 0) // Only render categories that have widgets
        .map((category) => (
          <div key={category.id} className="category">
            <h2>{category.name}</h2>
            <div className="widgets">
              {category.widgets.map((widget) => (
                <Widget
                  key={widget.id}
                  widget={widget}
                  categoryId={category.id}
                />
              ))}
            </div>
            <AddWidgetModal
        isOpen={isModalOpen}
        onClose={handleModalClose}
        onConfirm={handleConfirm}
        widgets={widgets}
      />
          </div>
        ))}

      {/* Show a message if no widgets match the search */}
      {filteredCategories.every((category) => category.widgets.length === 0) && (
        <p>No widgets match your search.</p>
      )}
    </div>
  );
};

export default Dashboard;
